        self.名称输入框._输入焦点 = false
        self.名称输入框._光标可视 = false
        self.介绍文本:清空()
        self.名称输入框:置可视(false, false)
        tp.进程 = 2
    elseif self.创建:事件判断() then
        if self.选中人物 == 0 then
            tp.提示:写入('#Y/请选择一个人物进入游戏')
        else
            if self.名称输入框:取文本() == '' then
                tp.提示:写入('#Y/请为角色取一个名字')
            else
                if #self.名称输入框:取文本() < 13 then
                    if 判断游戏名字(self.名称输入框:取文本()) == 1 then
                        tp.提示:写入('#Y/名字不能带有空格或者特殊符号')
                    else
                        if 开发调试 then
                            local pb_data = {
                                actor = gbk.toutf8(self.角色图片组[self.选中人物].模型),
                                name = gbk.toutf8(self.名称输入框:取文本()),
                                colorG = self.染色ID,
                                race = gbk.toutf8(self.角色图片组[self.选中人物].种族)
                            }
                            客户端:发送PB数据(1004, pb_data)
                        else
                            客户端:发送数据(3, self.角色图片组[self.选中人物].模型 .. fgf .. self.名称输入框:取文本() .. fgf .. self.染色ID)
                        end
                        self.名称输入框:置文本('')
                    end
                else
                    tp.提示:写入('#Y/角色的名字太长了。')
                end
            end
        end
    end
end

